import numpy as np

class landscape_icosahedral:
    def __init__(self, dim):
        self.dim = int(dim)
        if self.dim <= 0:
            raise ValueError("dim must be a positive integer")

        if self.dim >= 3:
            # icosahedron vertices embedded in the first three dimensions
            phi = (1.0 + np.sqrt(5.0)) / 2.0  # golden ratio
            s = 2.0  # scale so centers lie well within [-5,5]

            coords = []
            # set1: (0, ±1, ±phi)
            for s1 in [1, -1]:
                for s2 in [1, -1]:
                    coords.append([0.0, float(s1) * 1.0, float(s2) * phi])
            # set2: (±1, ±phi, 0)
            for a in [1, -1]:
                for b in [1, -1]:
                    coords.append([float(a) * 1.0, float(b) * phi, 0.0])
            # set3: (±phi, 0, ±1)
            for a in [1, -1]:
                for b in [1, -1]:
                    coords.append([float(a) * phi, 0.0, float(b) * 1.0])

            centers3d = np.array(coords, dtype=float) * s  # shape (12, 3)

            if self.dim > 3:
                centers = np.zeros((centers3d.shape[0], self.dim), dtype=float)
                centers[:, :3] = centers3d
            else:
                centers = centers3d  # shape (12, 3)
            self.centers = centers

        elif self.dim == 2:
            # A completely different 2D scheme: a 4x4 grid of centers (16 total)
            grid_vals = [-3.0, -1.0, 1.0, 3.0]
            centers = []
            for x in grid_vals:
                for y in grid_vals:
                    centers.append([x, y])
            self.centers = np.array(centers, dtype=float)

        else:  # self.dim == 1
            # 1D equally spaced centers along the line
            centers = []
            for x in np.linspace(-4.0, 4.0, 16):
                centers.append([float(x)])
            self.centers = np.array(centers, dtype=float)

        # Ensure centers shape is (N, dim)
        if self.centers.ndim == 1:
            self.centers = self.centers.reshape(-1, 1)

        # Normalize to explicit dimension
        if self.centers.shape[1] != self.dim:
            raise ValueError("Center dimensionality mismatch with problem dimension.")

    def f(self, x):
        x = np.asarray(x, dtype=float).reshape(-1)
        if x.shape[0] != self.dim:
            raise ValueError("Input x must have dimension {}".format(self.dim))

        # f(x) = min_j ||x - c_j||^2
        # Works for centers in any dimension; uses broadcasting for efficiency
        dist2 = np.sum((self.centers - x) ** 2, axis=1)
        return float(np.min(dist2))

import numpy as np

class landscape:

    def __init__(self, dim=5):
        self.dim = int(dim)

    def f(self, x):
        x = np.asarray(x)
        if x.ndim != 1:
            x = x.ravel()
        if x.size != self.dim:
            raise ValueError(f"Input dimension mismatch: expected {self.dim}, got {x.size}")
        # Toroidal, separable wells: distance to nearest integer on a periodic grid
        # Fractional part in [0, 1)
        frac = x - np.floor(x)
        # Distance to the nearest integer on a torus (accounts for wrap-around)
        dist_to_nearest_int = np.minimum(frac, 1.0 - frac)
        return float(np.sum(dist_to_nearest_int ** 2))

import numpy as np

class landscape_sine_interaction_torus:

    def __init__(self, dim=5, gamma=0.5):
        self.dim = int(dim)
        self.gamma = float(gamma)

    def f(self, x):
        x = np.asarray(x)
        if x.ndim != 1:
            x = x.ravel()
        if x.size != self.dim:
            raise ValueError(f"Input dimension mismatch: expected {self.dim}, got {x.size}")

        # Base periodic wells per dimension
        s = np.sin(np.pi * x)

        # Sum of squared per-dimension wells (nonnegative, minimal at integers)
        sum_sin2 = float(np.sum(s * s))

        # Cyclic cross-term to couple neighboring dimensions (non-separable)
        cross_sum = float(np.dot(s, np.roll(s, -1)))  # sum_i s_i * s_{i+1}

        # Squared cross-term keeps landscape phase-transition-free and basins uniform
        val = sum_sin2 + self.gamma * (cross_sum ** 2)

        return val
