import numpy as np

class landscape_radial_rings_homogeneous:
    def __init__(self, dim=5, a=1.0, b=0.9):
        self.dim = int(dim)
        self.a = float(a)
        self.b = float(b)
        self.lb = -5.0
        self.ub = 5.0

    def f(self, x):
        x = np.asarray(x, dtype=float).reshape(-1)
        if x.size != self.dim:
            raise ValueError("Input x must have dimension {}".format(self.dim))
        r = np.linalg.norm(x)
        return float(r * r + self.a * (1.0 - np.cos(self.b * r)))
