import numpy as np

class landscape:

    def __init__(self, dim=5):
        self.dim = dim
        # Global minimum at the origin (r = 0)
        self.r0 = 0.0
        # Small sinusoidal perturbation to introduce mild nonconvexity without breaking homogeneity
        self.amp = 0.2
        self.freq = 3.0

    def f(self, x):
        x = np.asarray(x, dtype=float)
        if x.ndim != 1 or x.shape[0] != self.dim:
            raise ValueError(f"x must be a 1D array of length {self.dim}")
        r = np.linalg.norm(x)
        return (r - self.r0)**2 + self.amp * np.sin(self.freq * r)

