class landscape_pairwise_cosine:
    def __init__(self, dim=5):
        self.dim = int(dim)
        # strength of the non-separable coupling between coordinate pairs
        self.beta = 0.6

    def f(self, x):
        x = np.asarray(x, dtype=float).reshape(-1)
        if x.size != self.dim:
            raise ValueError(f"x must have dimension {self.dim}, got {x.size}")
        # separable quadratic term
        val = np.sum(x * x)
        # non-separable pairwise term: sum over i<j of (1 - cos(x_i - x_j))
        if self.dim >= 2:
            diff = x[:, None] - x[None, :]
            iu = np.triu_indices(self.dim, k=1)
            pair_term = np.sum(1.0 - np.cos(diff[iu]))
            val += self.beta * pair_term
        return float(val)

