class landscape_anisotropic_ellipsoid:
    """
    A completely different landscape from the isotropic radial benchmark.
    This function is f(x) = ||A x||^2, where A is a fixed random full-rank matrix.
    It yields ellipsoidal level sets in the original space and has a unique global
    minimum at x = 0. The landscape is homogeneous (no phase transitions) and
    operates within [-5, 5]^dim.
    """
    def __init__(self, dim=5, seed=123):
        self.dim = int(dim)
        rng = np.random.default_rng(seed)
        # Generate a fixed full-rank matrix A (dim x dim)
        while True:
            self.A = rng.normal(size=(self.dim, self.dim))
            if np.linalg.matrix_rank(self.A) == self.dim:
                break

    def f(self, x):
        x = np.asarray(x, dtype=float)
        if x.ndim != 1:
            x = x.ravel()
        if x.shape[0] != self.dim:
            raise ValueError(f"Input dimension mismatch: expected {self.dim}, got {x.shape[0]}")
        y = self.A.dot(x)
        return float(np.dot(y, y))

