class landscape_cross_coupled_ring_quad:
    def __init__(self, dim=5, c=0.5):
        """
        dim: integer, dimensionality of the landscape
        c: coupling coefficient in [0, 1) for ring-adjacent interaction
           The objective is:
               f(x) = sum_i x_i^2 + c * sum_i x_i * x_{i+1 (mod dim)}
        This form is homogeneous of degree 2 (f(tx) = t^2 f(x)) and
        has a unique global minimum at x = 0 for 0 <= c < 1.
        """
        self.dim = int(dim)
        self.c = float(c)

        if self.dim <= 0:
            raise ValueError("dim must be a positive integer.")
        if not (0.0 <= self.c < 1.0):
            raise ValueError("c must be in [0, 1) to guarantee positive definiteness of the ring-coupled quadratic landscape.")

    def f(self, x):
        """
        Evaluate the landscape at a 1-D real-valued input x with length self.dim.
        x must be a real-valued array-like of length self.dim.
        """
        x = np.asarray(x, dtype=float).reshape(-1)
        if x.size != self.dim:
            raise ValueError(f"Input x must have length {self.dim}, got {x.size}")

        # f(x) = sum_i x_i^2 + c * sum_i x_i * x_{i+1 (mod dim)}
        self_sum_squares = float(np.dot(x, x))
        cross_sum = float(np.dot(x, np.roll(x, -1)))  # x_i * x_{i+1}
        val = self_sum_squares + self.c * cross_sum
        return float(val)

