class landscape_ring_coupled_homo:
    def __init__(self, dim=5, coupling=0.5, normalize=True):
        self.dim = int(dim)
        if self.dim < 2:
            raise ValueError("dim must be at least 2 for ring coupling.")
        self.coupling = float(coupling)
        self.normalize = bool(normalize)

    def f(self, x):
        x = np.asarray(x, dtype=float)
        if x.ndim != 1 or x.shape[0] != self.dim:
            raise ValueError(f"x must be a 1D array of length {self.dim}")
        # Base quadratic term
        base = np.dot(x, x)
        # Ring-coupled term: sum_i (x_i - x_{i+1})^2 with wrap-around
        diff = x - np.roll(x, -1)  # x_i - x_{i+1}
        cross = np.dot(diff, diff)
        # Normalize cross-term per-dimension to improve homogeneity across dim
        if self.normalize:
            cross = cross / float(self.dim)
        return base + self.coupling * cross

