        self.dim = int(dim)
        if self.dim < 1:
            raise ValueError("dim must be at least 1")
        # Internal, fixed parameters chosen to ensure smooth, monotonically increasing behavior in r
        self._alpha = 0.75  # radial growth rate
        self._scale = 1.0   # overall scaling factor

    def f(self, x):
        x = np.asarray(x, dtype=float)
        if x.ndim != 1 or x.shape[0] != self.dim:
            raise ValueError(f"x must be a 1D array of length {self.dim}")
        r = np.linalg.norm(x)
        return self._scale * (np.exp(self._alpha * r) - 1.0) ** 2

