class landscape_radial_smooth_bowl_homo_tanh_dimnorm:
    def __init__(self, dim=5, a=0.8, c=0.5, normalize_by_dim=True):
        """
        dim: dimensionality of the input space
        a: weight of the saturating cross-term (0 <= a)
        c: rate of tanh saturation (c > 0)
        normalize_by_dim: if True, normalize the radius squared by the dimensionality to preserve a
                          consistent appearance across different dimensionalities (homogeneity)
        """
        self.dim = int(dim)
        self.a = float(a)
        self.c = float(c)
        self.normalize_by_dim = bool(normalize_by_dim)

        if self.dim <= 0:
            raise ValueError("dim must be a positive integer.")
        if self.a < 0:
            raise ValueError("Parameter a must be non-negative.")

    def f(self, x):
        x = np.asarray(x, dtype=float)
        if x.ndim != 1:
            x = x.ravel()
        if x.size != self.dim:
            raise ValueError(f"Input x must have length {self.dim}, got {x.size}.")

        r2 = float(np.dot(x, x))
        if self.normalize_by_dim:
            r2_norm = r2 / float(self.dim)
        else:
            r2_norm = r2

        satur = float(np.tanh(self.c * r2_norm))
        val = r2_norm * (1.0 + self.a * satur)
        return float(val)

