class landscape_radial_quartic_homo:
    def __init__(self, dim=5, gamma=1.0, normalize=True):
        self.dim = int(dim)
        if self.dim < 1:
            raise ValueError("dim must be at least 1.")
        self.gamma = float(gamma)
        self.normalize = bool(normalize)

    def f(self, x):
        x = np.asarray(x, dtype=float)
        if x.ndim != 1 or x.shape[0] != self.dim:
            raise ValueError(f"x must be a 1D array of length {self.dim}")
        r2 = np.dot(x, x)
        if self.normalize:
            term1 = r2 / float(self.dim)
            term2 = self.gamma * (r2 * r2) / float(self.dim * self.dim)
        else:
            term1 = r2
            term2 = self.gamma * (r2 * r2)
        return term1 + term2
