import numpy as np

class landscape:

    def __init__(self, dim=5):
        self.dim = int(dim)
        if self.dim <= 0:
            raise ValueError("dim must be a positive integer.")
        # Rastringin-like base parameter (A n + sum(x_i^2 - A cos(2 pi x_i)))
        self.A = 10.0
        # Cross-term strength to induce nonseparability while preserving a unique global minimum at the origin
        self.gamma = 0.3

    def f(self, x):
        x = np.asarray(x, dtype=float).ravel()
        if x.size != self.dim:
            raise ValueError("Input vector length does not match landscape dimension.")

        # Base Rastringin-like term (separable component)
        base = self.A * self.dim + np.sum(x**2 - self.A * np.cos(2 * np.pi * x))

        # Nonseparable cross-term: sum_{i<j} (x_i - x_j)^2
        cross = self.gamma * 0.5 * np.sum((x[:, None] - x[None, :])**2)

        return float(base + cross)

import numpy as np

class wellfield_landscape:
    def __init__(self, dim=5):
        self.dim = int(dim)
        if self.dim <= 0:
            raise ValueError("dim must be a positive integer.")
        # Number of wells (including origin)
        self.M = max(4, 2 * self.dim)

        # Centers of Gaussian wells: deterministic pattern, origin included
        self.centers = self._generate_centers(self.M, self.dim)

        # Amplitudes for wells: deepest at origin, decreasing for others
        self.A = self._generate_amps(self.M)

        # Widths (standard deviations) for wells
        self.sigma = self._generate_sigma(self.M)

    def _generate_centers(self, M, dim):
        # First center is at the origin
        centers = [np.zeros(dim, dtype=float)]
        # Deterministic lattice of centers around the origin
        for k in range(1, M):
            t = k / float(M)
            # Simple deterministic pattern using sin with varying phase across dimensions
            c = 0.6 * np.array([np.sin(2.0 * np.pi * t * (i + 1)) for i in range(dim)], dtype=float)
            centers.append(c)
        return np.array(centers, dtype=float)

    def _generate_amps(self, M):
        # Deepest well at origin, amplitudes decay with k
        A0 = 7.0
        return A0 * (0.9 ** np.arange(M))

    def _generate_sigma(self, M):
        # Slightly increasing widths across wells (deterministic)
        return 0.25 + 0.15 * (np.arange(M) / max(1, (M - 1)))

    def f(self, x):
        x = np.asarray(x, dtype=float).ravel()
        if x.size != self.dim:
            raise ValueError("Input vector length does not match landscape dimension.")

        # Sum of negative Gaussians (wells) centered at deterministic centers
        val = 0.0
        for k in range(self.M):
            d = x - self.centers[k]
            dist2 = np.dot(d, d)
            val += -self.A[k] * np.exp(-dist2 / (2.0 * (self.sigma[k] ** 2)))

        # Add a smooth, separable positive base to push energy up away from wells
        val += np.sum(x ** 4)

        return float(val)

import numpy as np

class cos_quartic_multimodal:
    def __init__(self, dim=5, alpha=2.0, beta=2.0):
        self.dim = int(dim)
        if self.dim <= 0:
            raise ValueError("dim must be a positive integer.")
        self.alpha = float(alpha)  # amplitude of the cosine coupling
        self.beta = float(beta)    # frequency scaling for the cosine coupling

    def f(self, x):
        x = np.asarray(x, dtype=float).ravel()
        if x.size != self.dim:
            raise ValueError("Input vector length does not match landscape dimension.")

        # Base quartic, ensures a smooth global basin with minimum at origin
        base = np.sum(x ** 4)

        # Non-separable high-frequency coupling across all dimensions
        # Product of cos(beta * x_i) creates many local minima
        prod_cos = np.prod(np.cos(self.beta * x))

        # Global minimum at origin (f(0) = 0), with multiple local minima due to the cosine term
        val = base - self.alpha * prod_cos + self.alpha
        return float(val)
