import numpy as np

class landscape:

    def __init__(self, dim=5):
        self.dim = int(dim)

    def f(self, x):
        x = np.asarray(x, dtype=float)
        if x.size != self.dim:
            raise ValueError("Input x must have length {}".format(self.dim))

        # Ensure evaluation within the allowed domain
        x = np.clip(x, -5.0, 5.0)

        # 1D multimodal component with evenly spaced minima at t in {..., -4, -2, 0, 2, 4}
        # h(t) = 0.5 * (1 - cos(pi * t))  -> minima at t = 2k
        h = 0.5 * (1.0 - np.cos(np.pi * x))

        # The landscape is the sum of independent 1D wells across all dimensions
        return float(np.sum(h))
