import numpy as np

class landscape:
    def __init__(self, dim=5):
        self.dim = int(dim)
        # Parameters for the multi-frequency, homogeneous landscape
        self.A = 10.0  # amplitude for the first cosine term
        self.B = 5.0   # amplitude for the second cosine term
        self.D = self.A + self.B  # offset to ensure the global minimum is 0 at x=0

    def f(self, x):
        x = np.asarray(x, dtype=float)
        if x.ndim != 1:
            raise ValueError("Input x must be a 1-D array with length self.dim")
        if x.size != self.dim:
            raise ValueError("Dimension mismatch: expected dim={}, got {}".format(self.dim, x.size))

        # Per-dimension contribution with two cosine modulations (2*pi and 4*pi)
        per_dim = x**2 - self.A * np.cos(2 * np.pi * x) - self.B * np.cos(4 * np.pi * x) + self.D

        return float(np.sum(per_dim))

