import numpy as np

class landscape:

    def __init__(self, dim=5, seed=0):
        self.dim = int(dim)
        rng = np.random.default_rng(seed)
        # Per-dimension heterogeneity: amplitude and frequency differ across dimensions
        self.A = rng.uniform(0.4, 1.6, size=self.dim)  # amplitude of the cosine modulation
        self.B = rng.uniform(0.5, 3.0, size=self.dim)  # frequency of the cosine modulation

    def f(self, x):
        x = np.asarray(x, dtype=float)
        if x.ndim != 1 or x.shape[0] != self.dim:
            raise ValueError(f"Input x must be a 1D array of length {self.dim}.")
        # Separable landscape: sum over dimensions of [ x_i^2 + A_i * (1 - cos(B_i * x_i)) ]
        return float(np.sum(x**2 + self.A * (1.0 - np.cos(self.B * x))))

