class landscape_varied_freq_quadratic_cosine:
    def __init__(self, dim=5):
        self.dim = int(dim)
        if self.dim <= 0:
            raise ValueError("Dimension must be a positive integer.")

        i = np.arange(self.dim, dtype=float)

        # Per-dimension quadratic curvature (non-uniform across dimensions)
        self._a = 0.2 + 0.6 * (i / max(self.dim - 1, 1))  # range ~[0.2, 0.8]

        # Per-dimension cosine amplitude (ensures f(0) = 0 and heterogeneous basin shapes)
        self._b = 0.05 + 0.95 * np.abs(np.sin((i + 1) * np.pi / (self.dim + 1)))  # ~[0.05, ~1.0]

        # Per-dimension cosine frequency (diverse across dimensions)
        self._c = 0.5 + (i % 7) * 0.25  # frequencies cycling through {0.5, 0.75, 1.0, 1.25, 1.5, 1.75, 2.0, ...}

        self._a = np.asarray(self._a, dtype=float)
        self._b = np.asarray(self._b, dtype=float)
        self._c = np.asarray(self._c, dtype=float)

    def f(self, x):
        x = np.asarray(x, dtype=float)
        if x.shape[0] != self.dim:
            raise ValueError(f"Input x must have length {self.dim}, got {x.shape[0]}")
        # Separable: fi(xi) = ai * xi^2 + bi * (1 - cos(ci * xi))
        total = self._a * (x ** 2) + self._b * (1.0 - np.cos(self._c * x))
        return float(np.sum(total))
