import numpy as np

class landscape:
    def __init__(self, dim=5):
        self.dim = int(dim)

    def _rastrigin(self, x, freq):
        # Apply a per-dimension frequency (scaling) before computing Rastringin.
        y = freq * x
        A = 10.0
        # f = A*n + sum( y_i^2 - A*cos(2*pi*y_i) )
        return self.dim * A + np.sum(y**2 - A * np.cos(2 * np.pi * y))

    def f(self, x):
        x = np.asarray(x, dtype=float)
        if x.shape != (self.dim,):
            raise ValueError("Input x must be a 1D array of length self.dim")

        # Four regional Rastringin-like components with different frequencies
        freqs = [1.0, 0.5, 2.0, 0.8]

        # Use the first two coordinates to compute soft region weights
        if self.dim >= 2:
            u, v = x[0], x[1]
        else:
            u, v = (x[0], 0.0) if self.dim == 1 else (0.0, 0.0)

        def sigmoid(z):
            z = np.asarray(z, dtype=float)
            # Clip to avoid overflow in exp for extreme values
            z = np.clip(z, -50.0, 50.0)
            return 1.0 / (1.0 + np.exp(-z))

        # Soft-region weights (A,B,C,D) based on (u, v)
        s1, s2 = 5.0, 5.0
        w_A = sigmoid(s1 * u) * sigmoid(s2 * v)
        w_B = sigmoid(-s1 * u) * sigmoid(s2 * v)
        w_C = sigmoid(s1 * u) * sigmoid(-s2 * v)
        w_D = sigmoid(-s1 * u) * sigmoid(-s2 * v)

        weights = np.array([w_A, w_B, w_C, w_D], dtype=float)
        if weights.sum() == 0:
            weights = np.ones(4, dtype=float)

        # Compute the four regional Rastringin sub-functions
        f_k = np.zeros(4, dtype=float)
        for i, freq in enumerate(freqs):
            f_k[i] = self._rastrigin(x, freq)

        # Blend the regional functions with normalized weights
        blended = np.sum(weights * f_k) / (np.sum(weights) + 1e-12)
        return float(blended)

import numpy as np

class landscape:

    def __init__(self, dim):
        self.dim = int(dim)
        self.num_regions = 4  # number of non-homogeneous regimes

    def f(self, x):
        x = np.asarray(x, dtype=float).reshape(-1)
        if x.size != self.dim:
            raise ValueError(f"Input x must have dimension {self.dim}, got {x.size}")

        # Clipped to the search bounds [-5, 5]
        x = np.clip(x, -5.0, 5.0)

        # Phase-transition style region determination via a coarse tiling
        grid = 2.0  # cell size per dimension
        idx = np.floor((x + 5.0) / grid).astype(int)  # per-dimension cell indices
        region_id = int(np.mod(np.sum(idx), self.num_regions))

        if region_id == 0:
            # Region 0: Rastrigin-like, classic multimodal baseline
            return float(np.sum(x**2 - 10.0 * np.cos(2.0 * np.pi * x) + 10.0))
        elif region_id == 1:
            # Region 1: Quadratic + per-dimension cosine modulation with varying freq/ampl
            a1 = np.linspace(0.5, 1.5, self.dim)  # amplitudes per dimension
            b1 = np.linspace(1.5, 5.0, self.dim)  # frequencies per dimension
            return float(np.sum(x**2 + a1 * (1.0 - np.cos(b1 * x))))
        elif region_id == 2:
            # Region 2: Mix of sine-square and scaled quadratic terms -> many local minima
            p = np.linspace(0.5, 3.0, self.dim)
            q = np.ones(self.dim) * 0.6
            w = np.linspace(0.2, 1.0, self.dim)
            return float(np.sum((q * x)**2 + w * (np.sin(p * x))**2))
        else:
            # Region 3: Cosine-bump with a mild quadratic tilt
            r = np.linspace(0.8, 1.8, self.dim)
            s = np.linspace(2.0, 6.0, self.dim)
            t = 0.4
            return float(np.sum(r * (1.0 - np.cos(s * x)) + t * x**2))

import numpy as np

class landscape_four_region_blend:
    def __init__(self, dim=5):
        self.dim = int(dim)

        # Radial (normalized) centers and their Gaussian-like widths for four regions
        # r in [0,1], derived by normalizing by domain size 5 and sqrt(dim)
        self.r_centers = np.array([0.15, 0.38, 0.70, 0.92], dtype=float)
        self.r_sigmas = np.array([0.055, 0.08, 0.07, 0.04], dtype=float)

        # Angular centers for four sectors (east, north, west, south)
        self.angles = np.array([0.0, np.pi/2.0, np.pi, -np.pi/2.0], dtype=float)
        self.sigma_ang = np.pi / 6.0  # 30 degrees smoothness in sector blending

    def _angle_diff(self, a, b):
        d = a - b
        while d > np.pi:
            d -= 2.0 * np.pi
        while d <= -np.pi:
            d += 2.0 * np.pi
        return d

    # Base region functions (non-negative, all zero at the origin)
    def _g1(self, x):
        # Pure quartic term (non-negative, origin-minimizing)
        return float(np.sum(x**4))

    def _g2(self, x):
        # Non-negative, with per-dimension cosine modulation to create ridges
        return float(np.sum(x**2 * (1.0 + np.cos(2.0 * np.pi * x))))

    def _g3(self, x):
        # Non-negative, another cosine-based modulation that vanishes at origin
        return float(np.sum(x**2 * (1.0 - np.cos(2.0 * np.pi * x))))

    def _g4(self, x):
        # Cross-term squares to introduce non-separability (no negative values)
        val = 0.0
        for i in range(self.dim):
            for j in range(i + 1, self.dim):
                val += (x[i] * x[j])**2
        return float(val)

    def f(self, x):
        x = np.asarray(x, dtype=float)
        if x.shape != (self.dim,):
            raise ValueError("Input x must be a 1D array of length self.dim")

        # Normalize radius to [0,1] based on domain [-5,5]
        if self.dim > 0:
            r_unit = np.linalg.norm(x / 5.0) / np.sqrt(self.dim)
        else:
            r_unit = 0.0

        # Angle in first two dimensions (default to 0 if dim < 2)
        if self.dim >= 2:
            phi = np.arctan2(x[1], x[0])
        else:
            phi = 0.0

        # Radial base weights (centered at four radii)
        bases = []
        for rc, s in zip(self.r_centers, self.r_sigmas):
            bases.append(np.exp(-((r_unit - rc)**2) / (2.0 * (s**2))))
        bases = np.array(bases, dtype=float)

        # Angular weights (centered at four sector directions)
        ang_w = []
        for ang in self.angles:
            diff = self._angle_diff(phi, ang)
            ang_w.append(np.exp(- (diff**2) / (2.0 * (self.sigma_ang**2))))
        ang_w = np.array(ang_w, dtype=float)

        # Combine radial and angular weights (four-region blending)
        weights = bases * ang_w
        total = np.sum(weights)
        if total == 0.0:
            weights = np.ones(4, dtype=float) / 4.0
        else:
            weights = weights / total

        # Compute the four region subfunctions
        g1 = self._g1(x)
        g2 = self._g2(x)
        g3 = self._g3(x)
        g4 = self._g4(x)

        # Final landscape value: blend the four region functions
        fval = weights[0] * g1 + weights[1] * g2 + weights[2] * g3 + weights[3] * g4
        return float(fval)

