        self.dim = int(dim)
        # Choose a period (distance between adjacent minima) of 4.0 units
        # So minima are located at x = 4 * k for each dimension (k in Z)
        self.period = 4.0
        self.omega = 2.0 * np.pi / self.period

    def f(self, x):
        x = np.asarray(x)
        # Ensure 1D vector of correct length
        if x.ndim != 1:
            x = x.ravel()
        if x.shape[0] != self.dim:
