        self.A = 10.0
        # Deterministic phase offsets per dimension to create a non-trivial, reproducible basin layout
        self.phi = (np.arange(self.dim) * 0.42) % (2 * np.pi)

    def f(self, x):
        x = np.asarray(x, dtype=float)
        if x.shape[0] != self.dim:
