class landscape_uniform_basin:

    def __init__(self, dim=5):
        self.dim = dim
        # Five evenly spaced centers per dimension
        self.centers = np.array([-4.0, -2.0, 0.0, 2.0, 4.0], dtype=float)

    def f(self, x):
        x = np.asarray(x)
        if x.ndim != 1 or x.shape[0] != self.dim:
            raise ValueError("Input x must be a 1D numpy array of length dim.")
        # For each dimension, distance to the nearest center, squared
        # Shape: (dim, len(centers)) -> compute min over centers for each dimension
        distances = np.abs(x[:, None] - self.centers[None, :])  # broadcast to (dim, 5)
        min_dist = distances.min(axis=1)  # (dim,)
        per_dim = min_dist ** 2
        return float(np.sum(per_dim))

