class landscape_separable_weighted:
    """
    A strictly separable, multi-well landscape.
    f(x) = sum_i w[i] * sin^2(pi * x_i / 2)
    - Domain per dimension: [-5, 5]
    - Minima: x_i ∈ {-4, -2, 0, 2, 4} for all i, yielding 5^dim global optima
    - Independence: f(x) decomposes as a sum of independent 1D terms
    """
    def __init__(self, dim=5, seed=0, weights=None):
        self.dim = dim
        if weights is None:
            rng = np.random.default_rng(seed)
            # Weights in a range that preserves basin widths while varying depths
            self.weights = rng.uniform(0.5, 2.0, size=dim)
        else:
            w = np.asarray(weights)
            if w.shape != (dim,):
                raise ValueError("weights must be a 1D array of length 'dim'.")
            if np.any(w <= 0):
                raise ValueError("All weights must be positive.")
            self.weights = w

    def f(self, x):
        x = np.asarray(x)
        if x.ndim != 1 or x.shape[0] != self.dim:
            raise ValueError("Input x must be a 1D numpy array of length dim.")
        # Per-dimension term: w_i * sin^2(pi * x_i / 2)
        # Global function is the sum of independent contributions (separable)
        return float(np.dot(self.weights, np.sin(np.pi * x / 2.0) ** 2))

