class landscape_sine_wells:

    def __init__(self, dim=5):
        self.dim = dim
        # Periodicity in each dimension: zeros of sin^2(pi * x / period) occur every 'period'
        self.period = 2.0  # zeros at x in {..., -4, -2, 0, 2, 4, ...}
        # Optional centers (for documentation only; not strictly required for computation)
        self.centers = np.array([-4.0, -2.0, 0.0, 2.0, 4.0], dtype=float)

    def f(self, x):
        x = np.asarray(x, dtype=float)
        if x.ndim != 1 or x.shape[0] != self.dim:
            raise ValueError("Input x must be a 1D numpy array of length dim.")
        # Per-dimension term: sin^2(pi * x / period)
        per_dim = np.sin(np.pi * x / self.period) ** 2
        return float(np.sum(per_dim))
