class landscape_separable_sine_layered_multimodal:
    def __init__(self, dim=5, num_harmonics=3):
        self.dim = int(dim)
        if self.dim < 1:
            raise ValueError("Dimension must be at least 1.")
        self.num_harmonics = int(num_harmonics)

        # Deterministic per-dimension harmonic parameters to ensure reproducibility
        # Small, uniform amplitude to keep the inner term positive
        self.a = 0.15

        # Frequency and phase grids with deterministic variation across dimensions and harmonics
        self.b = np.zeros((self.dim, self.num_harmonics), dtype=float)
        self.phi = np.zeros((self.dim, self.num_harmonics), dtype=float)
        for i in range(self.dim):
            for h in range(self.num_harmonics):
                # Frequency grows with harmonic index and slightly with dimension index
                self.b[i, h] = (h + 1) * (0.6 + 0.25 * (i / max(self.dim - 1, 1)))
                # Phase offsets vary deterministically with i and h
                self.phi[i, h] = ((i + 1) * (h + 1)) * 0.9

    def f(self, x):
        x = np.asarray(x, dtype=float)
        if x.ndim != 1:
            x = x.ravel()
        if x.size != self.dim:
            raise ValueError(f"Input dimension mismatch: expected {self.dim}, got {x.size}")

        total = 0.0
        for i in range(self.dim):
            inner = 1.0  # base factor ensuring nonnegativity
            xi = x[i]
            for h in range(self.num_harmonics):
                inner += self.a * np.sin(self.b[i, h] * xi + self.phi[i, h])
            total += (xi ** 2) * inner
        return float(total)
